package io.github.ititus.math.number;


import org.junit.jupiter.api.Test;

import static io.github.ititus.math.number.BigIntegerMath.*;
import static java.math.BigInteger.*;
import static org.assertj.core.api.Assertions.assertThat;

public class BigIntegerFactorialTests {

    @Test
    public void fact_0() {
        assertThat(factorial(ZERO)).isEqualTo(ONE);
    }

    @Test
    public void fact_1() {
        assertThat(factorial(ONE)).isEqualTo(ONE);
    }

    @Test
    public void fact_2() {
        assertThat(factorial(TWO)).isEqualTo(TWO);
    }

    @Test
    public void fact_3() {
        assertThat(factorial(THREE)).isEqualTo(of(6));
    }

    @Test
    public void fact_4() {
        assertThat(factorial(of(4))).isEqualTo(of(24));
    }

    @Test
    public void fact_5() {
        assertThat(factorial(of(5))).isEqualTo(of(120));
    }

    @Test
    public void fact_6() {
        assertThat(factorial(of(6))).isEqualTo(of(720));
    }

    @Test
    public void fact_7() {
        assertThat(factorial(of(7))).isEqualTo(of(5040));
    }

    @Test
    public void fact_8() {
        assertThat(factorial(of(8))).isEqualTo(of(40320));
    }

    @Test
    public void fact_9() {
        assertThat(factorial(of(9))).isEqualTo(of(362880));
    }

    @Test
    public void fact_10() {
        assertThat(factorial(TEN)).isEqualTo(of(3628800));
    }

    @Test
    public void fact_100() {
        assertThat(factorial(of(100))).isEqualTo(of(
                "93326215443944152681699238856266700490715968264381621468592963895217599993229915608941463976156518286253697920827223758251185210916864000000000000000000000000"));
    }

    @Test
    public void fact_200() {
        assertThat(factorial(of(200))).isEqualTo(of(
                ""));
    }

    @Test
    public void fact_500() {
        assertThat(factorial(of(500))).isEqualTo(of(
                ""));
    }

    @Test
    public void fact_1000() {
        assertThat(factorial(of(1000))).isEqualTo(of(
                ""));
    }
}
